import React, { useState, useEffect } from 'react';
import { config } from './Config';
import { UserAgentApplication } from 'msal';
import { getUser } from './GraphService';
import { Button } from 'antd';
import 'antd/dist/antd.css';

const User = () => {

    const [users, setUser] = useState<any>();


    useEffect(() => {
      getUserService();
    },[]);

    const userAgentApplication = new UserAgentApplication({
        auth: {
            clientId: config.appId,
            redirectUri: config.redirectUri
        },
        cache: {
            cacheLocation: "sessionStorage",
            storeAuthStateInCookie: true
        }
    });

    const isInteractionRequired = (error: Error): boolean => {
        if (!error.message || error.message.length <= 0) {
            return false;
        }

        return (
            error.message.indexOf('consent_required') > -1 ||
            error.message.indexOf('interaction_required') > -1 ||
            error.message.indexOf('login_required') > -1
        );
    }

    const getAccessToken = async (scopes: string[]) => {

        try {
            var silentResult = await userAgentApplication.acquireTokenSilent({
                scopes: scopes
            });
            return silentResult.accessToken;
        } catch (err) {
            // If a silent request fails, it may be because the user needs
            // to login or grant consent to one or more of the requested scopes
            if (isInteractionRequired(err)) {
                var interactiveResult = await userAgentApplication.acquireTokenPopup({
                    scopes: scopes
                });

                return interactiveResult.accessToken;
            } else {
                throw err;
            }
        }
    }

    const getUserService = async () => {
        const accessToken = await getAccessToken(config.scopes);
        getUser(accessToken)
            .then(result =>{
                setUser(result)
                const {value}=result;
            console.log(result)
            });
    }

    return (
        <Button onClick={getUserService}>click</Button>
    );

}

export default User;
