import React from 'react';
import { Form, Input, Button, Switch } from 'antd';
import 'antd/dist/antd.css';
import { Row, Col } from 'antd';
import { createUser } from './GraphService';
import { config } from './Config';
import { UserAgentApplication } from 'msal';


const User = () => {

    const userAgentApplication = new UserAgentApplication({
        auth: {
            clientId: config.appId,
            redirectUri: config.redirectUri
        },
        cache: {
            cacheLocation: "sessionStorage",
            storeAuthStateInCookie: true
        }
    });

    const getAccessToken = async (scopes: string[]) => {
        var silentResult = await userAgentApplication.acquireTokenSilent({
            scopes: scopes
        });
        return silentResult.accessToken;
    }
    const passwordProfile = {
        "forceChangePasswordNextSignIn": true,
        "forceChangePasswordNextSignInWithMfa": false,
        "password": "string"
    }

    const getMailNickName = (email: string) => {
        let nickName = email.split('@');
        return nickName;
    }

    const onFinish = async (values: any) => {

        var accessToken = await getAccessToken(config.scopes);
        const { userPrincipalName } = values;
        let mailNickname = getMailNickName(userPrincipalName)[0]
        const requestBody = { ...values, mailNickname, passwordProfile: { ...passwordProfile } }

        const res= createUser('sa', requestBody)
        console.log(requestBody);
        cons
    };

    return (
        <>
            <Row>
                <Col span={6}></Col>
                <Col span={12}>
                    <Form
                        onFinish={onFinish}
                    >
                        <Form.Item
                            label='Account Enable'
                            name='accountEnabled'>
                            <Switch
                                checkedChildren="Yes" unCheckedChildren="No" defaultChecked={false} />
                        </Form.Item>

                        <Form.Item
                            label="Display Name"
                            name="displayName"
                            rules={[{ required: true, message: 'Please input your Display Name!' }]}
                        >
                            <Input />
                        </Form.Item>
                        <Form.Item
                            label="Email"
                            name="userPrincipalName"
                            rules={[{ message: 'Please input your Email' }]}
                        >
                            <Input />
                        </Form.Item>
                        <br />
                        <Form.Item>
                            <Button type="primary" htmlType="submit">
                                Create
                            </Button>
                        </Form.Item>
                    </Form>
                </Col>
                <Col span={6}></Col>
            </Row>

        </>
    );
};

export default User;
